<?phpnamespace app\admin\controller;use think\Controller;class Upload extends Controller{    public function index(){        //        $file = $this->request->file('file');        // 移动到框架应用根目录/public/uploads/ 目录下        if($file){        //  上传文件            $info = $file->validate(['size'=>512000,'ext'=>'jpg,png,webp,jpeg'])->move(ROOT_PATH . 'public' . DS . 'uploads');            if($info){                // 成功上传后 获取上传信息                //路径3                $imgpath = date('Ymd').'/'.$info->getFilename();                //返回上传的图片路径                return json([                    'code'=>200,                    'msg'=>'上传图片成功',                    'imagepath'=>'/hotel-admin/public/uploads/'.$imgpath                ]);            }else{                // 上传失败获取错误信息                echo $file->getError();            }        }    }}