<?phpnamespace app\admin\validate;use think\Validate;class Login extends Validate{    protected $rule =[        'username'=>'require',        'password'=>'require',        'oldpass'=>'require',        'newpass'=>'require',        'newpass2'=>'require|confirm:newpass'    ];    protected $message=[      'username'=>'用户名必填',      'password'=>'密码必填',      'oldpass'=>'原密码必填',      'newpass.require'=>'新密码必填',      'newpass2.require'=>'确认密码必填',      'newpass2.confirm'=>'两次密码保持一致'    ];    protected $scene = [        'login'=>['username','password'],        'editpass'=>['oldpass','newpass','newpass2']    ];}